package com.zhongjian.webserver.beanconfiguration;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.core.env.Environment;

import com.zhongjian.webserver.alipay.AlipayConfig;
import com.zhongjian.webserver.common.LoggingUtil;

@Configuration
public class AutoAlipayConfiuration {

	@Autowired
	private Environment env;

	@Bean
	public AlipayConfig aipayConfig() {
		if ("dev".equals(env.getActiveProfiles()[0])) {
			LoggingUtil.i("读取测试环境支付宝参数配置");
			AlipayConfig alipayConfig = new AlipayConfig();
			alipayConfig.setAPPID("2016082100305178");
			alipayConfig.setBUSINESSID("2088102172419687");
			alipayConfig.setRSA_PRIVATE_KEY("");
			alipayConfig.setALIPAY_PUBLIC_KEY("");
			alipayConfig.setNotify_url("http://e2fmrv.natappfree.cc/alipaytest/notify_url");
			alipayConfig.setURL("https://openapi.alipaydev.com/gateway.do");
			alipayConfig.setCHARSET("UTF-8");
			alipayConfig.setFORMAT("json");
			alipayConfig.setSIGNTYPE("RSA2");
			return alipayConfig;
		}else{
			LoggingUtil.i("读取正式环境支付宝参数配置");
			AlipayConfig alipayConfig = new AlipayConfig();
			alipayConfig.setAPPID("2018011001747966");
			alipayConfig.setBUSINESSID("2088621931824615");
			alipayConfig.setRSA_PRIVATE_KEY("");
			alipayConfig.setALIPAY_PUBLIC_KEY("");
			alipayConfig.setNotify_url("https://appnew.zhongjianmall.com/zjapp/v1/notify_url");
			alipayConfig.setURL("https://openapi.alipay.com/gateway.do");
			alipayConfig.setCHARSET("UTF-8");
			alipayConfig.setFORMAT("json");
			alipayConfig.setSIGNTYPE("RSA2");
			return alipayConfig;
		}
	}

}
